import networkx as nx

def calculate_straightness_centrality(graph, weight=None):
    nodes = list(graph.nodes)
    straightness = {node: 0.0 for node in nodes}
    shortest_paths = dict(nx.all_pairs_dijkstra_path_length(graph, weight=weight))

    for i, u in enumerate(nodes):
        total_distance = 0.0
        euclidean_total = 0.0
        for j, v in enumerate(nodes):
            if i != j:
                shortest_path_dist = shortest_paths[u][v]
                euclidean_dist = ((graph.nodes[u]['pos'][0] - graph.nodes[v]['pos'][0])**2 +
                                  (graph.nodes[u]['pos'][1] - graph.nodes[v]['pos'][1])**2)**0.5
                if shortest_path_dist > 0:
                    total_distance += euclidean_dist / shortest_path_dist
                euclidean_total += 1
        if euclidean_total > 0:
            straightness[u] = total_distance / euclidean_total

    return straightness
