import numpy as np
from osgeo import gdal

def flow_accumulation(dem_path, output_path):
    dem_dataset = gdal.Open(dem_path)
    dem_band = dem_dataset.GetRasterBand(1)
    dem_array = dem_band.ReadAsArray()
    rows, cols = dem_array.shape
    flow_acc = np.zeros_like(dem_array, dtype=np.float32)

    def calculate_flow(r, c):
        if r < 0 or r >= rows or c < 0 or c >= cols:
            return 0
        min_val = dem_array[r, c]
        min_r, min_c = r, c
        for dr in [-1, 0, 1]:
            for dc in [-1, 0, 1]:
                if dr == 0 and dc == 0:
                    continue
                nr, nc = r + dr, c + dc
                if 0 <= nr < rows and 0 <= nc < cols:
                    if dem_array[nr, nc] < min_val:
                        min_val = dem_array[nr, nc]
                        min_r, min_c = nr, nc
        if min_r == r and min_c == c:
            return 1
        return 1 + calculate_flow(min_r, min_c)

    for row in range(rows):
        for col in range(cols):
            flow_acc[row, col] = calculate_flow(row, col)

    driver = gdal.GetDriverByName("GTiff")
    out_dataset = driver.Create(output_path, cols, rows, 1, gdal.GDT_Float32)
    out_dataset.SetGeoTransform(dem_dataset.GetGeoTransform())
    out_dataset.SetProjection(dem_dataset.GetProjection())
    out_band = out_dataset.GetRasterBand(1)
    out_band.WriteArray(flow_acc)
    out_band.FlushCache()
    out_dataset = None
