import numpy as np

def calculate_spatial_exposure_index(population_grid, distance_decay_function, bandwidth):
    n = population_grid.shape[0]
    exposure_index = np.zeros_like(population_grid, dtype=float)

    for i in range(n):
        for j in range(n):
            if population_grid[i, j] > 0:
                distances = np.sqrt((np.arange(n)[:, None] - i)**2 + (np.arange(n)[None, :] - j)**2)
                decay_weights = distance_decay_function(distances, bandwidth)
                exposure_index[i, j] = np.sum(decay_weights * population_grid)

    return exposure_index

