import numpy as np

def detect_spatial_arrangements(matrix):
    rows, cols = matrix.shape
    arrangements = []

    for i in range(rows):
        for j in range(cols):
            if i > 0 and matrix[i, j] == matrix[i-1, j]:
                arrangements.append(((i, j), (i-1, j)))
            if i < rows-1 and matrix[i, j] == matrix[i+1, j]:
                arrangements.append(((i, j), (i+1, j)))
            if j > 0 and matrix[i, j] == matrix[i, j-1]:
                arrangements.append(((i, j), (i, j-1)))
            if j < cols-1 and matrix[i, j] == matrix[i, j+1]:
                arrangements.append(((i, j), (i, j+1)))

    return arrangements

